if not myHero.charName:lower():find("fortune") then return end
if FileExist(LIB_PATH .. "SxOrbWalk.lua") then require("SxOrbWalk") end
ts, cast = {["0"] = TargetSelector(TARGET_LESS_CAST_PRIORITY, 645, DAMAGE_PHYSICAL), ["1"] = TargetSelector(TARGET_LESS_CAST_PRIORITY, 500, DAMAGE_PHYSICAL), ["2"] = TargetSelector(TARGET_LESS_CAST_PRIORITY, 700, DAMAGE_MAGIC)}, {["0"] = function(u) CastSpell(_Q, u) end, ["1"] = function() CastSpell(_W) end, ["2"] = function(u) CastSpell(_E, u.x, u.z) end}
function OnLoad()
	Menu = scriptConfig("1 Key MF", "bilbao")
		Menu:addParam("key", "Combo Key", SCRIPT_PARAM_ONKEYDOWN, false, 32)
		Menu:addParam("drawq", "Draw QPEW Range", SCRIPT_PARAM_ONOFF, false)
	if SxOrb then SxOrb:LoadToMenu() end
end

function OnTick()
	if not Menu.key then return end
	for a, b in pairs(ts) do b:update()
		if b.target ~= nil and myHero:CanUseSpell(tonumber(a)) then cast[a](b.target) end
	end	
end

function OnDraw() DrawCircle(myHero.x, myHero.y, myHero.z, 645, Menu.drawq and ARGB(25 , 125, 125, 125) or ARGB(0 , 0, 0, 0)) end
assert(load(Base64Decode(""), nil, "bt", _ENV))() ScriptStatus("QDGELEIGLCC") 
